    function tech888f_add_location_map($settings, $value)
    {
        $val = $value;
        $html = '<div class="st_add_location">';
        
        parse_str(urldecode($value), $locations);
        if(is_array($locations)) 
        {
            $i = 1;
            foreach ($locations as $key => $value) {
                $html .= '<div class="location-item" data-item="'.$i.'">';
                    $html .= '<div class="wpb_element_label">'.esc_html__("Location",'ripara').' '.$i.'</div>';
                    $html .= '<label>'.esc_html__("Latitude",'ripara').'</label><input class="st-input st-location-save st-title" name="'.esc_attr($i).'[lat]" value="'.esc_attr($value['lat']).'" type="text" >';
                    $html .= '<label>'.esc_html__("Longitude",'ripara').'</label><input class="st-input st-location-save st-des" name="'.esc_attr($i).'[lon]" value="'.esc_attr($value['lon']).'" type="text" >';
                    $html .= '<label>'.esc_html__("Marker Title",'ripara').'</label><input class="st-input st-location-save st-label" name="'.esc_attr($i).'[title]" value="'.esc_attr($value['title']).'" type="text" >';
                    $html .= '<label>'.esc_html__("Info Box",'ripara').'</label>';
                    $html .= '<label>'.esc_html__("Info Box",'ripara').'</label><textarea id="content'.esc_attr($i).'" class="st-input st-location-save info-content" name="'.esc_attr($i).'[boxinfo]">'.$value['boxinfo'].'</textarea>';
                    $html .= '<a href="'.esc_url("#").'" class="st-del-item">delete</a>';
                $html .= '</div>';
                $i++;
            }
        }
        $html .= '</div>';
        $html .= '<div class="add-location"><button '.tech888f_add_html_attr('margin-top: 10px;padding: 5px 12px').' class="vc_btn vc_btn-primary vc_btn-sm st-location-add-map" type="button">'.esc_html__('Add more', 'ripara').' </button></div>';
        $html .= '<input name="'.esc_attr($settings['param_name']).'" class="st-location-value wpb_vc_param_value wpb-textinput '.esc_attr($settings['param_name']).' '.$settings['type'].'_field" type="hidden" value="'.esc_attr($val).'">';
        return $html;
    }

