            static function tech888f_add_custum_shortcode_param($name, $form_field_callback, $script_url = null)
            {
                return WpbakeryShortcodeParams::addField($name, $form_field_callback, $script_url);
            }
            static function _change_class($class_string, $tag)
            {
                if($tag=='vc_row' || $tag=='vc_row_inner') {
                    $class_string = str_replace('vc_row-fluid', '', $class_string);
                }

                if(defined ('WPB_VC_VERSION'))
                {
                    if(version_compare(WPB_VC_VERSION,'4.2.3','>'))
                    {
                        if($tag=='vc_column' || $tag=='vc_column_inner') {
                            $class_string=str_replace('vc_col', 'col', $class_string);
                        }
                    }else
                    {
                        if($tag=='vc_column' || $tag=='vc_column_inner') {
                            $class_string = preg_replace('/vc_span(\d{1,2})/', 'col-lg-$1', $class_string);
                        }
                    }
                }

                return $class_string;
            }

            static function _tech888f_number_field_shortcode_param($settings, $value)
            {
                $param_name = isset($settings['param_name']) ? $settings['param_name'] : '';
                $type = isset($settings['type']) ? $settings['type'] : '';
                $min = isset($settings['min']) ? $settings['min'] : '';
                $max = isset($settings['max']) ? $settings['max'] : '';
                $step = isset($settings['step']) ? $settings['step'] : '';
                $suffix = isset($settings['suffix']) ? $settings['suffix'] : '';
                $class = isset($settings['class']) ? $settings['class'] : '';
                $output_html = '<input type="number" min="'.esc_attr($min).'" max="'.esc_attr($max).'" step="'.esc_attr($step).'" class="wpb_vc_param_value st-vc-type-st-number form-control' . esc_attr($param_name . ' ' . $type . ' ' . $class) . '" name="' . esc_attr($param_name) . '" value="'.esc_attr($value).'"/>';
                if(!empty($suffix)){
                    $output_html .= '<span class="suffix">'.$suffix.'</span>';
                }

                return $output_html;
            }

            //Show Image
            static function _tech888f_image_show_shortcode_param($settings, $value){
                if(!empty($settings['element'])){
                    $element = $settings['element'];
                    $param_name = isset($settings['param_name']) ? $settings['param_name'] : '';
                    $title_view = isset($settings['title_view']) ? $settings['title_view'] : esc_html__('View demo','ripara');
                    $std = isset($settings['std']) ? $settings['std'] : '';
                    $url_image_default = get_template_directory_uri().'/assets/admin/image/s7up-image-check/'.$param_name.'/'.$std.'.jpg';
                    $html = '<div class="s7up_image_check" data-element="'.esc_attr($element).'" data-param_name="'.esc_attr($param_name).'" data-img_url="'.get_template_directory_uri().'/assets/admin/image/element_name/'.'">';
                    $html .= '<img class = "image_icon_mb" src="'.esc_url($url_image_default).'" alt="'.esc_attr__("Image","ripara").'" '.tech888f_add_html_attr('height:40px').'>';
                    $html .= '<span class="title-view-demo">'.$title_view.'</span><i class="fa fa-share" aria-hidden="true"></i></div>';

                    return $html;
                }else{
                    return false;
                }
            }
        }

